import json
import csv

dicts = json.load(open('data.json'))
fields = dicts[0].keys()
writer = csv.DictWriter(open('data.csv', 'w'), fields)
writer.writeheader()
for dict_ in dicts:
    # if dict_['start']:
    #    dict_['start'] = dict_['start'].split('T')[0]
    #if 'end' in dict_ and dict_['end']:
    #    dict_['end'] = dict_['end'].split('T')[0]
    dict_['title'] = dict_['title'].replace('@', '')
    dict_['title'] = ': '.join(dict_['title'].split(': ')[1:])
    dict_['tags'] = ', '.join(dict_['tags'])
    writer.writerow(dict_)

